#include <stdbool.h>

bool checkArray(int* nums, int numsSize, int k) {
    long long extraDecreases = 0; // Running total of extra decreases
    long long neededDecreases = 0; // Total decreases needed to make all elements 0

    for (int i = 0; i < numsSize; i++) {
        if (i >= k) {
            extraDecreases -= nums[i - k]; // Remove the effect of the decrease applied k elements ago
        }
        if (nums[i] + extraDecreases > 0) {
            neededDecreases = nums[i] + extraDecreases;
            if (i + k <= numsSize) {
                extraDecreases += neededDecreases; // Apply the decrease to the next k elements
                nums[i] = -extraDecreases; // Update the current element to reflect the total decreases applied
            } else {
                // If there are not enough elements left to apply the decrease, return false
                return false;
            }
        }
    }

    // Check if the last k elements have been properly decreased
    for (int i = numsSize - k; i < numsSize; i++) {
        if (i >= 0 && nums[i] + extraDecreases > 0) {
            return false;
        }
    }

    return true;
}
