#include <stdbool.h>

bool checkArray(int* nums, int numsSize, int k) {
    // Use an additional array to keep track of operations effect on the array
    long long *ops = (long long *)calloc(numsSize, sizeof(long long));
    if (!ops) return false; // Memory allocation failed

    long long sumOps = 0; // Sum of operations applied up to the current index

    for (int i = 0; i < numsSize; ++i) {
        sumOps += ops[i]; // Update the sum of operations based on previous steps
        long long diff = nums[i] - sumOps; // Calculate the difference needed to make nums[i] zero
        if (diff > 0) { // If diff is positive, we need to apply operations to make it zero
            if (i + k > numsSize) { // If there's not enough space to apply the operation, return false
                free(ops);
                return false;
            }
            sumOps += diff; // Update the sumOps with the operations applied at current index
            if (i + k < numsSize) ops[i + k] -= diff; // Propagate the effect of the operation to future elements
        }
        // If diff is less than or equal to 0, no need to apply any operation, just move forward
    }

    free(ops); // Free allocated memory
    return true; // If the loop completes, it means we can make all elements zero
}
