#include <stdbool.h>
#include <stdlib.h>

bool checkArray(int* nums, int numsSize, int k) {
    long long* diff = (long long*)calloc(numsSize + 1, sizeof(long long)); // Difference array
    if (!diff) return false; // Fail-safe for memory allocation

    long long currentOperation = 0; // Accumulate the effect of operations

    for (int i = 0; i < numsSize; ++i) {
        currentOperation += diff[i]; // Apply the effect of previous operations
        long long requiredDecrease = nums[i] - currentOperation; // Required decrease for current element
        if (requiredDecrease > 0) { // Need to apply operations
            if (i + k > numsSize) { // Can't apply operation if it goes beyond array bounds
                free(diff);
                return false;
            }
            currentOperation += requiredDecrease; // Apply operation
            diff[i] += requiredDecrease; // Update difference array at current position
            diff[i + k] -= requiredDecrease; // Cancel the operation effect outside of the subarray
        }
    }

    free(diff); // Clean up allocated memory
    return true;
}
