class Solution {
  bool checkArray(List<int> nums, int k) {
    int n = nums.length;
    List<int> diff = List.filled(n, 0); // Track adjustments needed per index
    int ops = 0; // Operations applied up to the current index

    for (int i = 0; i < n; i++) {
      nums[i] -= ops; // Apply all previous operations to current element
      if (nums[i] < 0) return false; // Cannot have negative values after operations

      if (i + k > n) { // Check if there's enough room left for k elements
        if (nums[i] != 0) return false; // If not, current element must already be zero
      } else {
        ops += nums[i]; // Add current element's value to total operations
        if (i + k < n) diff[i + k] += nums[i]; // Schedule subtraction from future operations
      }

      if (i + k < n) ops -= diff[i]; // Remove operations no longer affecting new elements
    }

    return true;
  }
}
