class Solution {
  bool checkArray(List<int> nums, int k) {
    int n = nums.length;
    List<int> prefixSum = List.filled(n + 1, 0); // To manage the impact of operations

    for (int i = 0; i < n; i++) {
      // Calculate the actual value at nums[i] after operations
      int actualValue = nums[i] - (prefixSum[i] - (i - k >= 0 ? prefixSum[i - k] : 0));
      if (actualValue < 0) return false; // If actual value is negative, return false

      // If actualValue is positive, we need to decrease the next k elements by actualValue
      if (actualValue > 0) {
        if (i + k > n) return false; // Not enough elements to perform the operation
        prefixSum[i] += actualValue; // Increase current prefix sum to reflect the operation
        prefixSum[i + 1] -= actualValue; // Decrease next element in prefix sum to balance
        if (i + k < n) prefixSum[i + k] += actualValue; // Counteract the operation at the end of k elements
      }
    }

    // Check if the last k-1 elements have been correctly reduced to 0
    for (int i = n - k + 1; i < n; i++) {
      if (nums[i] - (prefixSum[i] - (i - k >= 0 ? prefixSum[i - k] : 0)) != 0) {
        return false; // If any of the last k-1 elements is not 0, return false
      }
    }

    return true;
  }
}
