func checkArray(nums []int, k int) bool {
    n := len(nums)
    decrement := make([]int, n)
    currentDecrement := 0

    for i := 0; i < n; i++ {
        if i >= k {
            // Subtract the decrement that was added k steps ago, as its effect ends
            currentDecrement -= decrement[i-k]
        }

        // Calculate the actual value after applying all decrements so far
        nums[i] -= currentDecrement

        if nums[i] > 0 {
            // If the current element is greater than 0, we need to apply the operation
            if i+k > n {
                // If there's not enough space to apply the operation, return false
                return false
            }
            // Increment the decrement array and current decrement by the value needed to make nums[i] zero
            decrement[i] += nums[i]
            currentDecrement += nums[i]
            nums[i] = 0
        }
    }

    // If all operations are successful, return true
    return true
}
