func checkArray(nums []int, k int) bool {
    n := len(nums)
    if n < k {
        return false
    }

    diff := make([]int, n)
    sum := 0

    for i := 0; i < n; i++ {
        nums[i] -= sum
        if nums[i] < 0 {
            return false // If any number becomes negative, it's impossible
        }

        if i+k <= n {
            diff[i] = nums[i]
            sum += diff[i] // Add the difference to the sum for the next elements
            if i+k < n {
                sum -= diff[i+k] // Ensure to subtract the out-of-scope difference when it's no longer in effect
            }
        } else if nums[i] != 0 {
            return false // If there's not enough room to make more adjustments and the number isn't zero, it's impossible
        }
    }

    for i := n - k; i < n; i++ {
        if nums[i] != 0 { // Check if the last elements are zero
            return false
        }
    }

    return true
}
