func checkArray(nums []int, k int) bool {
    // Create an additional array to track the total decrement needed at each position.
    decrement := make([]int, len(nums))
    totalDecrement := 0

    for i := 0; i < len(nums); i++ {
        // Apply the total decrement accumulated so far to the current number.
        nums[i] -= totalDecrement
        if nums[i] < 0 {
            // If the number becomes negative, it's impossible to make all elements 0.
            return false
        }
        if i+k > len(nums) {
            // If the operation cannot be applied due to the subarray extending beyond the array length.
            if nums[i] != 0 {
                return false
            }
        } else {
            // Determine the additional decrement needed to make the current number 0.
            totalDecrement += nums[i]
            // Schedule the removal of this decrement once it's out of the scope of the next k elements.
            if i+k < len(nums) {
                decrement[i+k] = nums[i]
            }
        }
        // Remove the scheduled decrement as it goes out of scope.
        totalDecrement -= decrement[i]
    }

    return true
}
