class Solution {
    public boolean checkArray(int[] nums, int k) {
        // Create a difference array to keep track of the necessary adjustments.
        // The size is nums.length + 1 to handle the case where the last operation
        // extends to the end of the array without index out of bounds.
        int[] diff = new int[nums.length + 1];

        int count = 0; // This variable will keep track of the total adjustments needed.

        for (int i = 0; i < nums.length; i++) {
            // Update the current number by the sum of previous operations.
            if (i > 0) {
                diff[i] += diff[i - 1];
            }

            // Calculate the difference needed to make the current number zero.
            int needed = nums[i] - diff[i];

            // If the needed adjustments are more than 0, we perform operations.
            if (needed > 0) {
                // If there's not enough space left in the array for a full k-sized subarray operation,
                // it's impossible to make all elements zero.
                if (i + k > nums.length) {
                    return false;
                }

                // Apply the needed adjustment to the current and future elements.
                diff[i] += needed;
                diff[i + k] -= needed;

                // Accumulate the total adjustments needed.
                count += needed;
            }
        }

        // If we've gone through the entire array without encountering an impossible condition,
        // then it's possible to make all elements zero.
        return true;
    }
}
