class Solution {
    public boolean checkArray(int[] nums, int k) {
        // The total adjustments needed for each position, initialized to 0.
        long[] adjustments = new long[nums.length];
        long totalAdjustment = 0; // Total adjustments made so far.

        for (int i = 0; i < nums.length; i++) {
            // Apply previous adjustments to the current position.
            if (i > 0) {
                adjustments[i] += adjustments[i - 1];
            }

            // Calculate the current value after adjustments.
            long currentValue = nums[i] - adjustments[i];

            // If additional adjustments are needed to make currentValue zero.
            if (currentValue > 0) {
                totalAdjustment += currentValue;

                // Check if there's enough space to apply the operation.
                if (i + k > nums.length) {
                    return false; // Not enough space for k-sized operation.
                }

                // Apply the adjustment forward for k elements.
                adjustments[i] += currentValue;
                if (i + k < nums.length) {
                    adjustments[i + k] -= currentValue;
                }
            }
        }

        // If all elements can be adjusted to 0, return true.
        return true;
    }
}
