class Solution {
    public boolean checkArray(int[] nums, int k) {
        // Array to hold the required operations to make an element zero
        int[] operations = new int[nums.length];
        int op = 0; // Current number of operations to apply

        for (int i = 0; i < nums.length; i++) {
            // Adjust the number of operations based on previously applied operations
            if (i >= k) {
                op -= operations[i - k]; // Remove operations that are 'out of range'
            }

            // Calculate the difference needed to make the current element zero
            int diff = nums[i] - op;
            if (diff > 0) {
                // If diff is positive, we need more operations
                op += diff;
                operations[i] = diff;
            } else if (diff < 0) {
                // If the adjusted number is negative, it's impossible to balance the array
                return false;
            }

            // No need to explicitly handle diff == 0, as no additional operations are needed
        }

        // Check the last k elements for any 'overflow' operations
        for (int i = nums.length - k; i < nums.length; i++) {
            if (i >= 0) {
                op -= operations[i];
                if (op != 0) {
                    // If there are still operations needed beyond the end of the array, return false
                    return false;
                }
            }
        }

        return true;
    }
}
