/**
 * @param {number[]} nums
 * @param {number} k
 * @return {boolean}
 */
var checkArray = function(nums, k) {
    // Array to simulate the queue for decrement operations
    let decOps = new Array(nums.length).fill(0);
    let totalDec = 0; // Total decrements to apply at current position

    for (let i = 0; i < nums.length; i++) {
        // Apply decrements up to this point
        nums[i] -= totalDec;

        // Check if it's impossible to make this element 0
        if (nums[i] < 0) return false;

        // If the element is already 0, no need to add a new operation
        if (nums[i] > 0) {
            totalDec += nums[i]; // Increase total decrements by the amount needed for this element
            decOps[i] = nums[i]; // Record this operation

            // If we're at a point where a previous operation should no longer affect future elements
            if (i + k < nums.length) {
                totalDec -= decOps[i + k - k]; // Subtract the operation that 'exits' the subarray
            }
        }

        // Move the 'window' forward, ensuring we only subtract operations that are 'exiting' the window
        if (i >= k - 1) {
            totalDec -= decOps[i - k + 1];
        }
    }

    // If we've made it through the array without returning false, it's possible to make all elements 0
    return true;
};
