/**
 * @param {number[]} nums
 * @param {number} k
 * @return {boolean}
 */
var checkArray = function(nums, k) {
    let n = nums.length;
    let diff = new Array(n).fill(0);
    let totalDiff = 0;

    for (let i = 0; i < n; i++) {
        nums[i] -= totalDiff; // Apply the accumulated difference to the current element
        if (nums[i] < 0) {
            // It's impossible to make all elements 0 if any element becomes negative
            return false;
        }
        if (nums[i] > 0) {
            // If the current element needs to be decremented
            if (i + k > n) {
                // If there's not enough room for a subarray of size k starting at i, return false
                return false;
            }
            diff[i] += nums[i]; // Add the needed decrement to the difference array
            totalDiff += nums[i]; // Accumulate this difference
            if (i + k < n) {
                diff[i + k] -= nums[i]; // Subtract this difference when the subarray end is reached
            }
        }
        totalDiff -= diff[i]; // Adjust totalDiff for the next element
    }
    // If we can process all elements according to the rules, return true
    return true;
};
