class Solution {
    fun checkArray(nums: IntArray, k: Int): Boolean {
        val n = nums.size
        val diff = LongArray(n + 1) // Extra space for handling prefix sum easily

        var currentSum: Long = 0 // Tracks the current cumulative reduction required
        for (i in nums.indices) {
            currentSum += diff[i] // Apply previous reductions to the current element
            val actualValue = nums[i] - currentSum // The effective value after reductions

            if (actualValue < 0) return false // If any value becomes negative, it's not possible

            if (actualValue > 0) {
                if (i + k > n) return false // If there's not enough room to make a k-sized subarray reduction

                val reduction = actualValue
                currentSum += reduction // Apply this reduction
                diff[i + k] -= reduction // Mark the end of the current k-sized subarray to reverse the increase
            }
        }

        return true
    }
}
