class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $k
     * @return Boolean
     */
    function checkArray($nums, $k) {
        $totalSum = array_sum($nums);

        // If the total sum is not divisible by k, it's impossible to make all elements 0
        if ($totalSum % $k !== 0) {
            return false;
        }

        // The total number of operations needed to make the sum 0
        $totalOps = $totalSum / $k;

        // Count how many times each number appears
        $countNums = array_count_values($nums);

        foreach ($countNums as $num => $count) {
            // If a number is present more times than the totalOps, it's impossible to make all 0
            if ($num * $count > $totalOps) {
                return false;
            }
        }

        // If we pass all checks, it's possible to make all elements 0
        return true;
    }
}
