class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $k
     * @return Boolean
     */
    function checkArray($nums, $k) {
        $n = count($nums);
        $need = 0; // Tracks the total decrease needed up to the current position
        $operations = array_fill(0, $n, 0); // Tracks the operations applied at each position

        for ($i = 0; $i < $n; $i++) {
            if ($i >= $k) {
                // Subtract the operations that are 'exiting' their effect range as we move forward
                $need -= $operations[$i - $k];
            }
            $delta = $nums[$i] - $need; // The additional operations needed at this position
            if ($delta > 0) {
                $need += $delta; // Update the total decrease needed
                $operations[$i] = $delta; // Record the operations needed at this position
                if ($i + $k > $n) {
                    // If we can't apply the operation to a full subarray of size k, return false
                    return false;
                }
            }
        }

        // If we've managed to apply all necessary operations within the array bounds, return true
        return true;
    }
}
