impl Solution {
    pub fn check_array(nums: Vec<i32>, k: i32) -> bool {
        let n = nums.len();
        let k = k as usize;
        let mut diff = vec![0; n + 1]; // Create a difference array with one extra space for easier handling of the end condition

        let mut ops = 0; // Track the total adjustments (operations) needed
        for i in 0..n {
            ops += diff[i]; // Update operations based on previous adjustments
            let actual_value = nums[i] - ops; // Calculate the actual value after adjustments
            if actual_value < 0 {
                // It's impossible to have negative values if all operations are correct
                return false;
            }
            if actual_value > 0 {
                // If the actual value needs to be decreased to 0
                if i + k > n {
                    // If the subarray extends beyond the array's end, it's impossible to adjust
                    return false;
                }
                ops += actual_value; // Increase ops to reflect the need for decrementing this value to 0
                diff[i] += actual_value; // Record this operation in the difference array
                diff[i + k] -= actual_value; // Neutralize the effect after the subarray
            }
        }
        true
    }
}
