object Solution {
  def checkArray(nums: Array[Int], k: Int): Boolean = {
    val n = nums.length
    val diff = Array.ofDim[Int](n + 1) // Difference array

    for (i <- 0 until n) {
      if (i == 0) diff(i) = nums(i)
      else diff(i) = nums(i) - nums(i - 1)
    }

    var operations = 0 // Track how many operations we've applied
    for (i <- 0 until n) {
      if (i > 0) diff(i) += diff(i - 1) // Apply previous changes
      if (diff(i) != 0) { // If the current element is not zero, we need to apply operations
        if (i + k > n) return false // Cannot apply operation if subarray goes out of bounds
        val decrement = diff(i) // The amount we need to decrement to make this element zero
        diff(i) -= decrement // Apply the decrement to current position
        diff(i + k) += decrement // Reverse the decrement after k elements
        operations += Math.abs(decrement) // Count the total operations (absolute value in case of negative numbers)
      }
    }
    true
  }
}
