object Solution {
    def checkArray(nums: Array[Int], k: Int): Boolean = {
        val n = nums.length
        // Array to track extra operations needed due to previous changes.
        val extraOps = Array.ofDim[Int](n)
        var opsNeeded = 0

        for (i <- 0 until n) {
            // Apply any extra operations required from previous steps.
            if (i > 0) {
                extraOps(i) += extraOps(i - 1)
            }

            // Adjust the current number by the extra operations already applied.
            val adjustedNum = nums(i) - extraOps(i)

            if (adjustedNum > 0) {
                // Calculate how many operations are needed to make this element 0.
                val ops = adjustedNum
                opsNeeded += ops

                // If we're within bounds, spread out the effect of these operations.
                if (i + k <= n) {
                    extraOps(i) += ops
                    if (i + k < n) extraOps(i + k) -= ops
                } else {
                    // If we cannot make this element 0 with k operations, return false.
                    return false
                }
            }
        }

        true
    }
}
