object Solution {
  def checkArray(nums: Array[Int], k: Int): Boolean = {
    val n = nums.length
    // Array to keep track of operation effects.
    val opEffects = new Array[Int](n)
    var accumulatedOps = 0 // Accumulated operations to apply.

    for (i <- 0 until n) {
      if (i >= k) {
        // Subtract the effect of the operation that is k steps behind.
        accumulatedOps -= opEffects(i - k)
      }

      // Calculate the current value after applying all previous operations.
      val currentValue = nums(i) - accumulatedOps

      if (currentValue > 0) {
        // Calculate the number of operations required to reduce this value to 0.
        val opsRequired = currentValue

        // If we are within the bounds, apply these operations.
        if (i + k <= n) {
          accumulatedOps += opsRequired
          opEffects(i) = opsRequired
        } else {
          // If we are out of bounds, it's impossible to make all elements 0.
          return false
        }
      }
    }

    true
  }
}
