function checkArray(nums: number[], k: number): boolean {
    const n = nums.length;
    const diff = new Array(n).fill(0);
    let extraDecrement = 0;

    for (let i = 0; i < n; i++) {
        nums[i] -= extraDecrement; // Apply pending decrements
        if (nums[i] < 0) return false; // If over-decremented, return false

        // Calculate the required decrement to make current element 0
        const decrement = nums[i];
        if (decrement > 0) {
            if (i + k > n) return false; // If there's not enough space to apply operation, return false
            extraDecrement += decrement; // Update the total decrement to apply to future elements
            if (i + k < n) diff[i + k] += decrement; // Schedule future decrements
        }

        // Subtract scheduled decrement from extraDecrement to only affect elements within the current subarray of size k
        extraDecrement -= diff[i];
    }

    return true;
}
