public class Solution {
    public int MinOperations(string s1, string s2, int x) {
        int mismatch = 0;
        foreach (var i in Enumerable.Range(0, s1.Length)) {
            if (s1[i] != s2[i]) mismatch++;
        }

        // If mismatches are odd, making s1 equal to s2 is impossible
        if (mismatch % 2 != 0) return -1;

        // Calculate the minimum number of operations needed
        int operationCost = 0;
        int halfMismatch = mismatch / 2;

        // If x is greater than 2, flipping adjacent mismatches is always cheaper
        if (x > 2) {
            // Count how many mismatches can be paired adjacently
            int adjPair = 0;
            for (int i = 0; i < s1.Length - 1; i++) {
                if (s1[i] != s2[i] && s1[i + 1] != s2[i + 1]) {
                    // Flip both to match, cost is 1 per adjacent flip
                    adjPair++;
                    i++; // Skip the next bit since it's part of a processed pair
                }
            }

            int remainingPairs = halfMismatch - adjPair;
            operationCost = adjPair + remainingPairs * Math.Min(x, 2);
        } else {
            // When x is 2 or less, flipping any two bits at cost x is as good or better than adjacent flips
            operationCost = halfMismatch * x;
        }

        return operationCost;
    }
}
