class Solution {
public:
    int minOperations(string s1, string s2, int x) {
        int mismatches = 0;
        for (int i = 0; i < s1.length(); ++i) {
            if (s1[i] != s2[i]) {
                ++mismatches;
            }
        }

        // If mismatches are odd, return -1
        if (mismatches % 2 != 0) return -1;

        // Calculate the number of pairs needed to be flipped
        int pairs = mismatches / 2;

        // Calculate minimum cost
        int cost = 0;

        // If x is less than or equal to 2, then using the first operation for all pairs is cheaper or equal
        if (x <= 2) {
            cost = pairs * x;
        } else {
            // If x is greater than 2, use the second operation for adjacent mismatches
            // Since every operation flips 2 bits, every mismatch pair can be fixed with a single operation of cost 1
            cost = pairs;
        }

        return cost;
    }
};
