#include <stdbool.h>

int minOperations(char * s1, char * s2, int x) {
    int n = 0; // Length of strings
    while (s1[n]) ++n;

    int cost = 0;
    for (int i = 0; i < n;) {
        if (s1[i] != s2[i]) {
            // Find the length of the current sequence of mismatches
            int start = i;
            while (i < n && s1[i] != s2[i]) ++i;
            int length = i - start;

            if (length == 1) { // Isolated mismatch
                if (x > 1) {
                    // Prefer flipping with another isolated mismatch if cheaper
                    cost += x;
                } else {
                    // If x <= 1, flip adjacent bits
                    cost += 1;
                }
            } else { // Consecutive mismatches
                cost += (length / 2) + (length % 2) * x;
            }
        } else {
            ++i;
        }
    }

    return cost;
}
