class Solution {
    public int minOperations(String s1, String s2, int x) {
        int n = s1.length();
        int diffPairs = 0; // To count pairs of differences

        // Calculate the number of mismatched pairs
        for (int i = 0; i < n; i++) {
            if (s1.charAt(i) != s2.charAt(i)) {
                // If we find a mismatch, we look for a pair
                if (i + 1 < n && s1.charAt(i + 1) != s2.charAt(i + 1)) {
                    diffPairs++;
                    i++; // Skip the next character since it's part of a handled pair
                }
            }
        }

        int mismatched = 0; // Count of individual mismatches not part of pairs
        // For each mismatch that wasn't part of a pair, we have an unpaired mismatch
        for (int i = 0; i < n; i++) {
            if (s1.charAt(i) != s2.charAt(i)) mismatched++;
        }
        mismatched -= 2 * diffPairs; // Remove mismatches that were part of diffPairs

        // If there are odd mismatches remaining, it's impossible
        if (mismatched % 2 != 0) return -1;

        int cost = 0;
        // Add cost for paired mismatches. Prefer adjacent swaps if cheaper than x.
        cost += Math.min(x, 2) * diffPairs;

        // For unpaired mismatches, they must be handled in pairs with cost x,
        // but since they are already in pairs, we consider if any adjustment is needed.
        // No additional cost calculation here for unpaired as they've been handled in pairs calculation.

        return cost;
    }
}
