/**
 * @param {string} s1
 * @param {string} s2
 * @param {number} x
 * @return {number}
 */
var minOperations = function(s1, s2, x) {
    let diffCount = 0;
    for (let i = 0; i < s1.length; i++) {
        if (s1[i] !== s2[i]) {
            diffCount++;
        }
    }

    // If the number of differing bits is odd, return -1
    if (diffCount % 2 !== 0) {
        return -1;
    }

    // The number of operations needed is diffCount / 2 since each operation flips 2 bits
    let operationsNeeded = diffCount / 2;

    // If x >= 2, it's always better or equal to flip adjacent bits with the second operation
    if (x >= 2) {
        return operationsNeeded; // Each operation costs 1
    } else {
        // If x < 2, potentially use the first operation, but since we can't flip non-adjacent
        // bits to match without guaranteeing they're part of a minimal operation set,
        // and x < 2 makes single flips cheaper, we still use the adjacent flip.
        // This part is actually redundant given the constraints and problem statement, but kept for clarity.
        return operationsNeeded * x;
    }
};
