class Solution {
    fun minOperations(s1: String, s2: String, x: Int): Int {
        var cost = 0
        var i = 0
        val n = s1.length
        while (i < n) {
            if (s1[i] != s2[i]) {
                // If the next character also differs and flipping them together is cheaper or equal,
                // it's better to flip them together with cost 1.
                if (i < n - 1 && s1[i + 1] != s2[i + 1]) {
                    cost += 1
                    i += 2
                } else {
                    // If we can only flip one character or flipping two non-consecutive characters is cheaper
                    if (x <= 2) {
                        // Impossible to match s1 with s2 as we need to flip only one bit here.
                        return -1
                    } else {
                        cost += x
                        i++ // Move to the next character as we have handled this character.
                    }
                }
            } else {
                i++ // No operation needed, move to the next character.
            }
        }
        return cost
    }
}
