class Solution {
    fun minOperations(s1: String, s2: String, x: Int): Int {
        var cost = 0
        var i = 0
        val n = s1.length
        var mismatches = 0 // Count individual mismatches

        // First, count the mismatches and pairs of mismatches that can be flipped directly
        while (i < n) {
            if (s1[i] != s2[i]) {
                // If the next character is also a mismatch, we can flip them together at the cost of 1
                if (i < n - 1 && s1[i + 1] != s2[i + 1]) {
                    cost += 1 // Flipping a pair of adjacent mismatches
                    i += 2
                    continue
                } else {
                    mismatches++ // Counting single mismatches for potential later flips
                }
            }
            i++
        }

        // If the cost of flipping two non-adjacent bits (x) is less than or equal to 2, use it for single mismatches
        if (x <= 2) {
            cost += mismatches * x
        } else {
            // If x is more than 2, flipping non-adjacent bits individually is not cost-effective,
            // so we should flip adjacent bits where possible, otherwise, it's impossible
            if (mismatches % 2 == 0) {
                cost += (mismatches / 2) * 2 // Pair the mismatches and flip adjacent at cost of 2 per pair
            } else {
                return -1 // Odd number of mismatches cannot be resolved with given operations
            }
        }

        return cost
    }
}
