class Solution {

    /**
     * @param String $s1
     * @param String $s2
     * @param Integer $x
     * @return Integer
     */
    function minOperations($s1, $s2, $x) {
        $n = strlen($s1);
        $mismatches = 0;

        for ($i = 0; $i < $n; $i++) {
            if ($s1[$i] != $s2[$i]) {
                $mismatches++;
            }
        }

        // If mismatches are odd, return -1
        if ($mismatches % 2 != 0) {
            return -1;
        }

        // Calculate the pairs of mismatches
        $mismatchPairs = $mismatches / 2;

        // If flipping a pair is cheaper or equal, use the pair flip operation
        if ($x <= 2) {
            return $mismatchPairs * $x;
        } else {
            // Otherwise, flip adjacent bits for 1 cost each
            return $mismatchPairs * 2; // Since each mismatched pair can be fixed with two adjacent flips
        }
    }
}
