class Solution {
    /**
     * @param String $s1
     * @param String $s2
     * @param Integer $x
     * @return Integer
     */
    function minOperations($s1, $s2, $x) {
        $n = strlen($s1);
        $diffCount = 0;

        // Count the number of positions where s1 and s2 differ
        for ($i = 0; $i < $n; $i++) {
            if ($s1[$i] !== $s2[$i]) {
                $diffCount++;
            }
        }

        // If the number of differences is odd, it's impossible to make the strings equal
        if ($diffCount % 2 !== 0) return -1;

        // Calculate the minimum operations for making s1 equal to s2
        $minOperations = 0;
        $pairsToFlip = intdiv($diffCount, 2); // Each operation flips two bits

        // The strategy to minimize operations changes based on the cost of x relative to the number of mismatched pairs
        if ($x > 2) {
            // When x is greater than 2, flipping adjacent bits (cost=1) is always cheaper than flipping non-adjacent bits (cost=x)
            // Since we need to flip pairs to match, each pair requires at least 2 operations
            $minOperations = $pairsToFlip * 2;
        } else {
            // If x is less than or equal to 2, it's more cost-effective or equally cost-effective to flip non-adjacent bits
            $minOperations = $pairsToFlip * $x;
        }

        return $minOperations;
    }
}
