impl Solution {
    pub fn min_operations(s1: String, s2: String, x: i32) -> i32 {
        let (mut diff_01, mut diff_10) = (0, 0);
        for (char1, char2) in s1.chars().zip(s2.chars()) {
            if char1 != char2 {
                if char1 == '0' {
                    diff_01 += 1;
                } else {
                    diff_10 += 1;
                }
            }
        }

        // If the total number of mismatches is odd, return -1
        if (diff_01 + diff_10) % 2 != 0 {
            return -1;
        }

        // Calculate the minimum cost
        let min_cost = if x > 2 {
            // If x is greater than 2, always prefer adjacent flips (cost of 1)
            diff_01.min(diff_10) + ((diff_01.max(diff_10) - diff_01.min(diff_10)) / 2)
        } else {
            // If x is 2 or less, flipping two arbitrary bits might be cheaper
            diff_01.min(diff_10) * x + ((diff_01.max(diff_10) - diff_01.min(diff_10)) / 2)
        };

        min_cost
    }
}
