impl Solution {
    pub fn min_operations(s1: String, s2: String, x: i32) -> i32 {
        let n = s1.len();
        let mut mismatches = 0;

        // Counting the mismatches
        for (a, b) in s1.chars().zip(s2.chars()) {
            if a != b {
                mismatches += 1;
            }
        }

        // If mismatches are odd, return -1 as it's impossible to make them equal
        if mismatches % 2 != 0 {
            return -1;
        }

        // Total mismatches that can be corrected by flipping adjacent bits
        let mut adjacent_flips = 0;
        let mut i = 0;
        while i < n {
            // If there's a mismatch and the next bit is also a mismatch and differs from the current,
            // it means we can flip these two adjacent bits.
            if i + 1 < n && s1.chars().nth(i) != s2.chars().nth(i) && s1.chars().nth(i) != s1.chars().nth(i + 1) {
                adjacent_flips += 1;
                i += 2; // Skip the next bit as it's part of the resolved mismatch
            } else {
                i += 1;
            }
        }

        // Remaining mismatches that need to be corrected individually
        let individual_flips = (mismatches / 2) - adjacent_flips;

        // Calculate the cost based on the number of adjacent flips (cost 1 each) and individual flips (cost x each)
        let cost = adjacent_flips + individual_flips * x;
        cost
    }
}
