object Solution {
    def minOperations(s1: String, s2: String, x: Int): Int = {
        val n = s1.length
        var i = 0
        var totalCost = 0

        while (i < n) {
            // Find the start of a differing segment
            if (s1(i) != s2(i)) {
                var j = i
                // Find the end of the differing segment
                while (j + 1 < n && s1(j + 1) != s2(j + 1)) {
                    j += 1
                }
                // Calculate the length of the segment
                val len = j - i + 1
                // Calculate the cost for this segment
                len match {
                    case 1 => totalCost += 1 // Cannot use x operation for single difference, flip adjacent
                    case _ => 
                        // For more than 2 differences, check if using x operation is cheaper
                        val costUsingX = if (len % 2 == 0) len / 2 * x else (len - 1) / 2 * x + 1
                        totalCost += Math.min(costUsingX, len)
                }
                // Move to the next segment
                i = j
            }
            i += 1
        }

        totalCost
    }
}
