function minOperations(s1: string, s2: string, x: number): number {
  let cost = 0;
  let differences = 0;

  // Count differences and identify positions
  for (let i = 0; i < s1.length; i++) {
    if (s1[i] !== s2[i]) {
      differences++;
    }
  }

  // If differences are odd, it's impossible to make them equal
  if (differences % 2 !== 0) return -1;

  // Main loop to adjust s1 to match s2
  for (let i = 0; i < s1.length - 1; i++) {
    // Check for adjacent differences to flip at the cost of 1
    if (s1[i] !== s2[i] && s1[i + 1] !== s2[i + 1] && s1[i] !== s1[i + 1]) {
      cost += 1; // Apply the second operation
      i++; // Skip the next bit as it's already handled
    }
  }

  // Calculate remaining differences after adjacent flips
  differences = differences / 2 - cost;

  // Apply the first operation for remaining non-adjacent differences
  cost += differences * x;

  return cost;
}
