    let cost = 0;
    let i = 0;

    // Iterate over the strings to find differences and calculate the cost of making them equal
    while (i < s1.length) {
        if (s1[i] !== s2[i]) {
            // If the next character is also different and matches s1[i], flip both at the cost of 1
            if (i + 1 < s1.length && s1[i + 1] !== s2[i + 1] && s1[i] !== s1[i + 1]) {
                cost += 1; // Cost for flipping adjacent bits
                i += 2; // Skip the next character as it's already handled
            } else {
                // If flipping non-adjacent bits is cheaper or necessary
                if (x < 2) {
                    // When flipping any two bits is cheaper or the only option left
                    cost += x;
                } else {
                    // If only one bit needs to be flipped and flipping adjacent bits isn't possible
                    return -1; // It's impossible to make s1 equal to s2 with the given operations
