function minOperations(s1: string, s2: string, x: number): number {
    let diff = 0;
    for (let i = 0; i < s1.length; i++) {
        if (s1[i] !== s2[i]) {
            diff++;
        }
    }

    // If the difference is odd, it's impossible to make them equal
    if (diff % 2 !== 0) return -1;

    // Calculate minimum operations for flipping bits
    let minOps = 0;
    let i = 0;
    while (i < s1.length) {
        if (s1[i] !== s2[i]) {
            // Look for a pair to flip
            if (i + 1 < s1.length && s1[i + 1] !== s2[i + 1]) {
                minOps += 1; // Cost for adjacent flip is always 1
                i += 2; // Skip the next bit as it's been handled
            } else {
                // If no adjacent mismatch, consider the cost of flipping with x
                minOps += x;
                i++; // Move to the next bit
            }
        } else {
            i++; // Move to the next bit if they are equal
        }
    }

    return minOps;
}
