public class Solution {
    public long MaximumSumOfHeights(IList<int> maxHeights) {
        int n = maxHeights.Count;
        long maxSum = 0;

        // Iterate through each position as the peak of the mountain
        for (int peak = 0; peak < n; peak++) {
            long sum = maxHeights[peak];
            int leftMax = maxHeights[peak];
            int rightMax = maxHeights[peak];

            // Expand to the left
            for (int left = peak - 1; left >= 0; left--) {
                leftMax = Math.Min(leftMax, maxHeights[left]);
                sum += leftMax;
            }

            // Expand to the right
            for (int right = peak + 1; right < n; right++) {
                rightMax = Math.Min(rightMax, maxHeights[right]);
                sum += rightMax;
            }

            // Update maxSum if this configuration has a higher sum
            maxSum = Math.Max(maxSum, sum);
        }

        return maxSum;
    }
}
