#include <vector>
#include <algorithm>

class Solution {
public:
    long long maximumSumOfHeights(vector<int>& maxHeights) {
        int n = maxHeights.size();
        long long maxSum = 0;

        for (int peak = 0; peak < n; ++peak) {
            // Calculate sum for the current peak
            long long sum = maxHeights[peak];
            int leftHeight = maxHeights[peak];
            int rightHeight = maxHeights[peak];

            // Expand to the left of the peak
            for (int i = peak - 1; i >= 0; --i) {
                leftHeight = min(leftHeight - 1, maxHeights[i]);
                sum += max(leftHeight, 1); // Ensure height is at least 1
            }

            // Expand to the right of the peak
            for (int i = peak + 1; i < n; ++i) {
                rightHeight = min(rightHeight - 1, maxHeights[i]);
                sum += max(rightHeight, 1); // Ensure height is at least 1
            }

            // Update maxSum if the current sum is greater
            maxSum = max(maxSum, sum);
        }

        return maxSum;
    }
};
