#include <vector>
#include <algorithm>

class Solution {
public:
    long long maximumSumOfHeights(vector<int>& maxHeights) {
        int n = maxHeights.size();
        vector<int> leftMax(n), rightMax(n);

        // Precompute the maximum height from the left
        leftMax[0] = maxHeights[0];
        for (int i = 1; i < n; ++i) {
            leftMax[i] = min(maxHeights[i], leftMax[i - 1] + 1);
        }

        // Precompute the maximum height from the right
        rightMax[n - 1] = maxHeights[n - 1];
        for (int i = n - 2; i >= 0; --i) {
            rightMax[i] = min(maxHeights[i], rightMax[i + 1] + 1);
        }

        // Calculate the maximum sum of heights
        long long maxSum = 0;
        for (int i = 0; i < n; ++i) {
            // Ensure the height respects both left and right constraints
            int height = min(leftMax[i], rightMax[i]);
            maxSum += height;
        }

        return maxSum;
    }
};
