#include <stdio.h>

long long maximumSumOfHeights(int* maxHeights, int maxHeightsSize) {
    int left[maxHeightsSize], right[maxHeightsSize];
    long long sum = 0;

    // Left to right pass
    left[0] = maxHeights[0];
    for (int i = 1; i < maxHeightsSize; i++) {
        left[i] = (left[i - 1] + 1 < maxHeights[i]) ? left[i - 1] + 1 : maxHeights[i];
    }

    // Right to left pass
    right[maxHeightsSize - 1] = maxHeights[maxHeightsSize - 1];
    for (int i = maxHeightsSize - 2; i >= 0; i--) {
        right[i] = (right[i + 1] + 1 < maxHeights[i]) ? right[i + 1] + 1 : maxHeights[i];
    }

    // Calculate the sum of minimum of left and right for each position
    for (int i = 0; i < maxHeightsSize; i++) {
        sum += (left[i] < right[i]) ? left[i] : right[i];
    }

    return sum;
}
