#include <stdio.h>

long long maximumSumOfHeights(int* maxHeights, int maxHeightsSize) {
    // Pre-calculate the max possible height from both sides for each position
    int leftMax[maxHeightsSize], rightMax[maxHeightsSize];
    long long maxSum = 0;

    // Calculate left max heights
    leftMax[0] = maxHeights[0];
    for (int i = 1; i < maxHeightsSize; ++i) {
        leftMax[i] = (maxHeights[i] < leftMax[i - 1] + 1) ? maxHeights[i] : leftMax[i - 1] + 1;
    }

    // Calculate right max heights
    rightMax[maxHeightsSize - 1] = maxHeights[maxHeightsSize - 1];
    for (int i = maxHeightsSize - 2; i >= 0; --i) {
        rightMax[i] = (maxHeights[i] < rightMax[i + 1] + 1) ? maxHeights[i] : rightMax[i + 1] + 1;
    }

    // For each position as peak, calculate the maximum sum of heights
    for (int i = 0; i < maxHeightsSize; ++i) {
        long long sum = 0;
        // Use the minimum of left and right max heights for each tower to ensure mountain shape
        for (int j = 0; j < maxHeightsSize; ++j) {
            int minHeight = (leftMax[j] < rightMax[j]) ? leftMax[j] : rightMax[j];
            sum += minHeight;
        }
        if (sum > maxSum) {
            maxSum = sum;
        }
    }

    return maxSum;
}
