import 'dart:math';

class Solution {
  int maximumSumOfHeights(List<int> maxHeights) {
    int n = maxHeights.length;
    // Initialize the arrays to track the max possible height so far from both sides.
    List<int> leftMax = List<int>.filled(n, 0);
    List<int> rightMax = List<int>.filled(n, 0);
    int maxSum = 0;

    leftMax[0] = maxHeights[0];
    // Populate leftMax with the highest possible tower height from the left up to each point.
    for (int i = 1; i < n; i++) {
      leftMax[i] = min(leftMax[i - 1] + 1, maxHeights[i]);
    }

    rightMax[n - 1] = maxHeights[n - 1];
    // Populate rightMax with the highest possible tower height from the right up to each point.
    for (int i = n - 2; i >= 0; i--) {
      rightMax[i] = min(rightMax[i + 1] + 1, maxHeights[i]);
    }

    // Calculate the maximum sum of heights by considering the minimum of leftMax and rightMax
    // at each position, which ensures the mountain property.
    for (int i = 0; i < n; i++) {
      maxSum += min(leftMax[i], rightMax[i]);
    }

    return maxSum;
  }
}
