/**
 * @param {number[]} maxHeights
 * @return {number}
 */
var maximumSumOfHeights = function(maxHeights) {
    const n = maxHeights.length;
    let leftMax = new Array(n).fill(0);
    let rightMax = new Array(n).fill(0);
    let maxSum = 0;

    // Calculate maximum heights from the left
    leftMax[0] = maxHeights[0];
    for (let i = 1; i < n; i++) {
        leftMax[i] = Math.min(maxHeights[i], leftMax[i-1] + 1);
    }

    // Calculate maximum heights from the right
    rightMax[n-1] = maxHeights[n-1];
    for (let i = n-2; i >= 0; i--) {
        rightMax[i] = Math.min(maxHeights[i], rightMax[i+1] + 1);
    }

    // Calculate the maximum possible sum of heights
    for (let i = 0; i < n; i++) {
        maxSum += Math.min(leftMax[i], rightMax[i]);
    }

    return maxSum;
};
