/**
 * @param {number[]} maxHeights
 * @return {number}
 */
var maximumSumOfHeights = function(maxHeights) {
    const n = maxHeights.length;
    let dpLeft = new Array(n).fill(0);
    let dpRight = new Array(n).fill(0);

    dpLeft[0] = maxHeights[0];
    for (let i = 1; i < n; i++) {
        dpLeft[i] = Math.min(dpLeft[i-1] + 1, maxHeights[i]);
    }

    dpRight[n-1] = maxHeights[n-1];
    for (let i = n - 2; i >= 0; i--) {
        dpRight[i] = Math.min(dpRight[i+1] + 1, maxHeights[i]);
    }

    let maxSum = 0;
    for (let i = 0; i < n; i++) {
        // The height at each position is the minimum between the left and right DP values
        // This ensures the mountain property while maximizing the height
        let minHeight = Math.min(dpLeft[i], dpRight[i]);
        maxSum += minHeight;
    }

    return maxSum;
};
