class Solution {
    fun maximumSumOfHeights(maxHeights: List<Int>): Long {
        val n = maxHeights.size
        var maxSum: Long = 0

        // Iterate through all possible peaks
        for (peak in maxHeights.indices) {
            var sum: Long = maxHeights[peak].toLong()
            var minHeightLeft = maxHeights[peak]
            var minHeightRight = maxHeights[peak]

            // Form mountain to the left of the peak
            for (i in peak - 1 downTo 0) {
                minHeightLeft = minOf(minHeightLeft - 1, maxHeights[i])
                sum += minHeightLeft
            }

            // Form mountain to the right of the peak
            for (i in peak + 1 until n) {
                minHeightRight = minOf(minHeightRight - 1, maxHeights[i])
                sum += minHeightRight
            }

            // Update maxSum if this formation has a higher sum
            maxSum = maxOf(maxSum, sum)
        }

        return maxSum
    }
}
