class Solution {
    fun maximumSumOfHeights(maxHeights: List<Int>): Long {
        val n = maxHeights.size
        val leftMax = IntArray(n)
        val rightMax = IntArray(n)

        // Compute maximum height from left to right
        leftMax[0] = maxHeights[0]
        for (i in 1 until n) {
            leftMax[i] = minOf(leftMax[i - 1] + 1, maxHeights[i])
        }

        // Compute maximum height from right to left
        rightMax[n - 1] = maxHeights[n - 1]
        for (i in n - 2 downTo 0) {
            rightMax[i] = minOf(rightMax[i + 1] + 1, maxHeights[i])
        }

        // Calculate the maximum possible sum
        var maxSum: Long = 0
        for (i in 0 until n) {
            maxSum += minOf(leftMax[i], rightMax[i])
        }

        return maxSum
    }
}
