class Solution {
    fun maximumSumOfHeights(maxHeights: List<Int>): Long {
        val n = maxHeights.size
        val leftMax = LongArray(n) { 0 }
        val rightMax = LongArray(n) { 0 }

        // Calculate left maximum heights
        leftMax[0] = maxHeights[0].toLong()
        for (i in 1 until n) {
            leftMax[i] = minOf(leftMax[i - 1] + 1, maxHeights[i].toLong())
        }

        // Calculate right maximum heights
        rightMax[n - 1] = maxHeights[n - 1].toLong()
        for (i in n - 2 downTo 0) {
            rightMax[i] = minOf(rightMax[i + 1] + 1, maxHeights[i].toLong())
        }

        // Calculate the maximum possible sum of heights
        var maxSum: Long = 0
        for (i in 0 until n) {
            maxSum += minOf(leftMax[i], rightMax[i])
        }

        return maxSum
    }
}
