class Solution {

    /**
     * @param Integer[] $maxHeights
     * @return Integer
     */
    function maximumSumOfHeights($maxHeights) {
        $n = count($maxHeights);
        $peakIndex = array_search(max($maxHeights), $maxHeights); // Find the peak index
        $heights = array_fill(0, $n, 0); // Initialize the heights array
        $heights[$peakIndex] = $maxHeights[$peakIndex]; // Set the peak height

        // Build the left side of the mountain
        for ($i = $peakIndex - 1; $i >= 0; $i--) {
            $heights[$i] = min($maxHeights[$i], $heights[$i + 1]);
        }

        // Build the right side of the mountain
        for ($i = $peakIndex + 1; $i < $n; $i++) {
            $heights[$i] = min($maxHeights[$i], $heights[$i - 1]);
        }

        // Calculate the sum of heights
        $sum = array_sum($heights);
        return $sum;
    }
}
