class Solution {

    /**
     * @param Integer[] $maxHeights
     * @return Integer
     */
    function maximumSumOfHeights($maxHeights) {
        $n = count($maxHeights);
        $maxSum = 0;

        for ($peak = 0; $peak < $n; $peak++) {
            $currentSum = $maxHeights[$peak];
            $leftMax = $maxHeights[$peak];
            $rightMax = $maxHeights[$peak];

            // Build towards the left
            for ($i = $peak - 1; $i >= 0; $i--) {
                $leftMax = min($leftMax - 1, $maxHeights[$i]); // Ensure it's strictly increasing towards the peak
                $currentSum += max(0, $leftMax); // Avoid negative heights
            }

            // Build towards the right
            for ($i = $peak + 1; $i < $n; $i++) {
                $rightMax = min($rightMax - 1, $maxHeights[$i]); // Ensure it's strictly decreasing after the peak
                $currentSum += max(0, $rightMax); // Avoid negative heights
            }

            $maxSum = max($maxSum, $currentSum); // Update max sum if this peak offers a better sum
        }

        return $maxSum;
    }
}
