class Solution(object):
    def maximumSumOfHeights(self, maxHeights):
        """
        :type maxHeights: List[int]
        :rtype: int
        """
        def calculate_max_sum(heights):
            return sum(min(prefix_max[i], suffix_max[i]) for i in range(len(heights)))

        n = len(maxHeights)
        prefix_max = maxHeights[:]
        suffix_max = maxHeights[:]

        # Forward pass to enforce increasing property
        for i in range(1, n):
            prefix_max[i] = min(prefix_max[i], prefix_max[i - 1] + 1)

        # Backward pass to enforce decreasing property
        for i in range(n - 2, -1, -1):
            suffix_max[i] = min(suffix_max[i], suffix_max[i + 1] + 1)

        # Calculate the maximum possible sum
        return calculate_max_sum(maxHeights)
